    val name: String,
    val url: String?
) : Serializable

data class Publication1 (
    val name: String,
    val logo: String?
) : Serializable

class Repository {
    companion object {
        private val timezone = TimeZone.getTimeZone("America/New_York")
        private val formatter = SimpleDateFormat("dd-mm-yyyy hh:mm:ss", Locale.ENGLISH)

        private val testArticle = Article1(
